    text-shadow: 0 2px 10px rgba(0,0,0,0.2);
    color: white !important;
}

.about-hero .breadcrumb {
    background: rgba(255,255,255,0.1);
    border-radius: 25px;
    padding: 10px 20px;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255,255,255,0.2);
    display: inline-flex;
    align-items: center;
